/**
 * Use the Cordova Media recorder for recording and encoding audio
 * on a WebView, wrapped in a native App.
 * Supports 3GPP recording on Android, WAV recording on iOS.
 *
 * More information on:
 * https://github.com/apache/cordova-plugin-media/blob/master/doc/index.md
 */
export default class CordovaMediaRecorder {
  /**
   * MediaRecorder.
   */
  constructor() {
    this._isRecording = false;

    const platform = device.platform;
    let filepath = null;
    if (platform === 'Android') {
      // See the 'cordova-plugin-media' documentation for more Android quirks:
      // Android devices record audio in Adaptive Multi-Rate format.
      // The specified file should end with a .3gp extension.
      this.filename = 'recording.3gp';
      this.mimetype = 'audio/3gpp';
      // Attempts to save the file on some specific location failed.
      // Therefore, the path is no longer specified, just the filename.
      // On iOS the behaviour is documented under quirks:
      // If a full path is not provided, it's saved in
      // LocalFileSystem.TEMPORARY
      // On Android, a temporary recording is always saved to:
      // /storage/emulated/0/tmprecording.3gp
      // Then moved to its final destination:
      // /storage/emulated/0/<filename>
      filepath = this.filename;
    } else if (platform === 'iOS') {
      // iOS only records to files of type .wav and returns an error if the
      // file name extension is not correct.
      this.filename = 'recording.wav';
      this.mimetype = 'audio/wav';
      // On iOS, the path fed to Media() should be prepended with
      // 'documents://'. This behaviour is documented as quirk.
      filepath = 'documents://' + this.filename;
    } else {
      throw new Error('Unable to detect Android or iOS platform for ' +
        'determining audio format.');
    }

    this.mediaRecorder = new Media(filepath,
      // success callback
      () => {
        console.log('Final recording written to: ' + filepath);
      },

      // error callback
      err => {
        console.debug('recordAudio(): Audio Error: ' + err.code);
      }
    );
  }

  /**
   * Start recording audio.
   */
  record() {
    this.mediaRecorder.startRecord();
    this._isRecording = true;
  }

  /**
   * Is audio recording in progress.
   *
   * @returns {boolean} True when recording. False otherwise.
   */
  isRecording() {
    return this._isRecording;
  }

  /**
   * Stop recording audio.
   */
  stop() {
    if (this.isRecording()) {
      this.mediaRecorder.stopRecord();
      this._isRecording = false;
    }
  }

  _requestFilepath(
    filename, callback) {
    // org.apache.cordova.file provides the HTML5 Filesystem API.

    // var fs = window.TEMPORARY;
    const fs = window.PERSISTENT;

    window.requestFileSystem(
      fs, 0, fileSystemCallback, fileSystemErrorCallback);

    function fileSystemCallback(fileSystem) {
      // www.w3.org/TR/2012/WD-file-system-api-20120417/#idl-def-FileSystem
      console.debug('Got filesystem name: ' + fileSystem.name);

      // getFile Creates or looks up a file.
      // By setting options to create:false, only a lookup will be performed.
      console.debug('Calling getFile in read mode: ' + filename);
      fileSystem.root.getFile(filename, {
        create: false
      },
        entryCallback, entryErrorCallback);
    }

    function fileSystemErrorCallback(domError) {
      console.debug('Error calling requestFileSystem: ' + domError.name);
    }

    function entryCallback(entry) {
      // http://www.w3.org/TR/2012/WD-file-system-api-20120417/#idl-def-Entry
      console.debug('Got file entry: ' + entry.name);
      entry.file(callback);
    }

    function entryErrorCallback(fileError) {
      console.debug('Error calling getFile: ' + fileError.code);
    }
  }

  /**
   * Request encoded audio to be returned through callback.
   *
   * @param {Function} callback - The callback to use when returning the audio as a blob.
   */
  getEncodedAudio(callback) {
    this._requestFilepath(this.filename, readDataUrl);

    function readDataUrl(file) {
      const reader = new FileReader();
      reader.onloadend = function(evt) {
        const arraybuffer = evt.target.result;
        const b = new Blob([arraybuffer], {
          type: this.mimetype
        });
        callback(b);
      };

      reader.readAsArrayBuffer(file);
    }
  }
}

/*
 * Use the Media Stream Recording API for recording and encoding. Ogg Opus
 * (audio/ogg) is the prefered output format.
 *
 * The Media Stream Recording API is W3C standard in the making:
 * https://dvcs.w3.org/hg/dap/raw-file/tip/media-stream-capture/RecordingProposal.html
 *
 * Currently only supported in Firefox. There's a standards author working for
 * Microsoft which hints wide adoption in the future.
 *
 * Encoder only supports 48k/16k mono audio channel.
 * http://dxr.mozilla.org/mozilla-central/source/content/media/MediaRecorder.cpp
 * https://wiki.mozilla.org/Gecko:MediaRecorder
 *
 */
export default class MediaRecorder {
  /**
   * MediaRecorder.
   *
   * @param {MediaStream} mediaStream - The MediaStream to analyze.
   */
  constructor(mediaStream) {
    const self = this;
    this.mediaRecorder = new window.MediaRecorder(mediaStream);
    this.mediaRecorder.ondataavailable = function(e) {
      self.recordedBlob = new Blob([e.data], {
        type: 'audio/ogg'
      });
      console.log('Recorded audio/ogg Blob size: ' + self.recordedBlob.size);
      if (self.callback) {
        self.callback(self.recordedBlob);
        self.callback = null;
      }
    };
  }

  /**
   * Start recording audio.
   */
  record() {
    this.recordedBlob = null;
    this.callback = null;
    this.mediaRecorder.start();
  }

  /**
   * Is audio recording in progress.
   *
   * @returns {boolean} True when recording. False otherwise.
   */
  isRecording() {
    return this.mediaRecorder.state === 'recording';
  }

  /**
   * Stop recording audio.
   */
  stop() {
    if (this.isRecording()) {
      // Calling `stop()` throws a dataavailable event.
      this.mediaRecorder.stop();
    }
  }

  /**
   * Request encoded audio to be returned through callback.
   *
   * @param {Function} callback - The callback to use when returning the audio as a blob in Ogg Opus format.
   */
  getEncodedAudio(callback) {
    if (this.recordedBlob) {
      // Data already available, return right away.
      callback(this.recordedBlob);
      return;
    }
    // Callback will trigger later when audio is ready.
    this.callback = callback;
  }
}

/**
 * Packer class for audio packing
 *
 * @private
 */
export default class WavePacker {
  /**
   * Stop recording audio.
   *
   * @param {number} recordingSampleRate - Sample rate of recording. Must be either 48000 or 44100.
   * @param {number} sampleRate - Sample rate. Must be half or a quarter of the recording sample rate.
   * @param {number} channels - Amount of audio channels. 1 or 2.
   */
  init(recordingSampleRate, sampleRate, channels) {
    this.recordingSampleRate = recordingSampleRate;
    if ([48000, 44100].indexOf(this.recordingSampleRate) === -1) {
      throw new Error(
        '48000 or 44100 are the only supported recordingSampleRates');
    }

    this.sampleRate = sampleRate;
    if ([
      this.recordingSampleRate,
      this.recordingSampleRate / 2,
      this.recordingSampleRate / 4
    ].indexOf(this.sampleRate) === -1) {
      throw new Error(
        'sampleRate must be equal, half or a quarter of the ' +
        'recording sample rate');
    }

    this.channels = channels;
    this.recording = false;
  }

  clear() {
    this.recLength = 0;
    this.recBuffersL = [];
    this.recBuffersR = [];
  }

  record(left, right) {
    console.debug('Recording bytes: ' + left.length);
    this.recBuffersL.push(left);
    this.recBuffersR.push(right);
    this.recLength += left.length;
  }

  recordStreaming(left, right, callback) {
    function convertFloat32ToInt16(buffer) {
      let l = buffer.length;
      const buf = new Int16Array(l);
      while (l--) {
        buf[l] = Math.min(1, buffer[l]) * 0x7FFF;
      }
      return buf.buffer;
    }
    // Both the left and right channel's data is a view (Float32Array)
    // on top of the buffer (ArrayBuffer). Each buffer's element should
    // have _value between -1 and 1.
    // The audio to export are 16 bit PCM samples that are wrapped in
    // a WAVE file at the server. Therefore convert from float here.
    const converted = convertFloat32ToInt16(left);
    console.debug('Streaming bytes: ' + converted.byteLength);
    callback(converted);
  }

  exportWAV(callback) {
    const bufferL = WavePacker.mergeBuffers(this.recBuffersL, this.recLength);
    const bufferR = WavePacker.mergeBuffers(this.recBuffersR, this.recLength);
    const interleaved = this.interleave(bufferL, bufferR);
    const dataview = this.encodeWAV(interleaved);
    const audioBlob = new Blob([dataview], {
      type: 'audio/wav'
    });
    callback(audioBlob);
  }


  exportMonoWAV(callback) {
    const bufferL = WavePacker.mergeBuffers(this.recBuffersL, this.recLength);
    const dataview = this.encodeWAV(bufferL, true);
    const audioBlob = new Blob([dataview], {
      type: 'audio/wav'
    });
    callback(audioBlob);
  }

  /**
   * Wrap the raw audio in a header to make it a WAVE format.
   *
   * Specs: {@link https://ccrma.stanford.edu/courses/422/projects/WaveFormat/}.
   *
   * @param {[]} interleaved - Array of interleaved audio.
   */
  encodeWAV(interleaved) {
    const buffer = new ArrayBuffer(44 + interleaved.length * 2);
    const view = new DataView(buffer);

    // RIFF chunk descriptor
    WavePacker.writeUTFBytes(view, 0, 'RIFF');
    // file length
    view.setUint32(4, 44 + interleaved.length * 2, true);
    // RIFF type
    WavePacker.writeUTFBytes(view, 8, 'WAVE');
    // FMT sub-chunk
    WavePacker.writeUTFBytes(view, 12, 'fmt ');
    // format chunk length
    view.setUint32(16, 16, true);
    // sample format (raw)
    view.setUint16(20, 1, true);
    // channel count. mono=1, stereo=2
    view.setUint16(22, this.channels, true);
    // sample rate
    view.setUint32(24, this.sampleRate, true);
    // byte rate (sample rate * block align)
    view.setUint32(28, this.sampleRate * 2 * this.channels, true);
    // block align (channel count * bytes per sample)
    view.setUint16(32, this.channels * 2, true);
    // bits per sample
    view.setUint16(34, 16, true);
    // data sub-chunk
    WavePacker.writeUTFBytes(view, 36, 'data');
    view.setUint32(40, interleaved.length * 2, true);

    // write the PCM samples
    const lng = interleaved.length;
    let index = 44;
    const volume = 1;
    for (let i = 0; i < lng; i++) {
      view.setInt16(index, interleaved[i] * (0x7FFF * volume), true);
      index += 2;
    }

    // Wrap in HTML5 Blob for transport
    const blob = new Blob([view], {
      type: 'audio/wav'
    });
    console.log('Recorded audio/wav Blob size: ' + blob.size);
    return blob;
  }

  interleave(leftChannel, rightChannel) {
    let result = null;
    let length = null;
    let i = null;
    let inputIndex = null;
    if (this.channels === 1) {
      // Keep both right and left input channels, but "pan" them both
      // in the center (to the single mono channel)
      length = leftChannel.length;
      result = new Float32Array(length);
      for (i = 0; i < leftChannel.length; ++i) {
        result[i] = 0.5 * (leftChannel[i] + rightChannel[i]);
      }
    } else {
      length = leftChannel.length + rightChannel.length;
      result = new Float32Array(length);

      inputIndex = 0;
      for (i = 0; i < length;) {
        result[i++] = leftChannel[inputIndex];
        result[i++] = rightChannel[inputIndex];
        inputIndex++;
      }
    }

    // Also downsample if needed.
    if (this.recordingSampleRate !== this.sampleRate) {
      // E.g. 44100/11025 = 4
      const reduceBy = this.recordingSampleRate / this.sampleRate;
      const resampledResult = new Float32Array(length / reduceBy);

      inputIndex = 0;
      for (i = 0; i < length;) {
        let value = 0;
        for (let j = 0; j < reduceBy; j++) {
          value += result[inputIndex++];
        }
        resampledResult[i++] = 1 / reduceBy * value;
      }
      return resampledResult;
    }
    return result;
  }

  static mergeBuffers(channelBuffer, recordingLength) {
    const result = new Float32Array(recordingLength);
    let offset = 0;
    const lng = channelBuffer.length;
    for (let i = 0; i < lng; i++) {
      const buffer = channelBuffer[i];
      result.set(buffer, offset);
      offset += buffer.length;
    }
    return result;
  }

  static writeUTFBytes(view, offset, string) {
    const lng = string.length;
    for (let i = 0; i < lng; i++) {
      view.setUint8(offset + i, string.charCodeAt(i));
    }
  }
}

/**
 * @title ITSLanguage Javascript Audio
 * @overview This is part of the ITSLanguage Javascript SDK to perform audio related functions.
 * @copyright (c) 2014 ITSLanguage
 * @license MIT
 * @author d-centralize
 *
 * This class fires the same events as the HTML5 Audio does. {@link http://www.w3schools.com/tags/ref_av_dom.asp}
 */

export default class WebAudioPlayer {
  /**
   * ITSLanguage WebAudioPlayer non-graphical component.
   *
   * This player uses the HTML5 Audio component for playback.
   *
   * @param {Object} [options] - Override any of the default settings.
   */
  constructor(options) {
    this._settings = Object.assign({}, options);

    this._initPlayer();
  }

  _initPlayer() {
    this.sound = new window.Audio();
    this._pauseIsStop = false;

    // The its.AudioPlayer API is based upon the same API calls as the
    // HTML5 Audio element itself, therefore, just bubble up all events.
    const self = this;

    this.sound.addEventListener('playing', () => {
      if (self._settings.playingCb) {
        self._settings.playingCb();
      }
    });

    this.sound.addEventListener('timeupdate', () => {
      if (self._settings.timeupdateCb) {
        self._settings.timeupdateCb();
      }
    });

    this.sound.addEventListener('durationchange', () => {
      if (self._settings.durationchangeCb) {
        self._settings.durationchangeCb();
      }
    });

    this.sound.addEventListener('canplay', () => {
      if (self._settings.canplayCb) {
        self._settings.canplayCb();
      }
    });

    this.sound.addEventListener('ended', () => {
      if (self._settings.endedCb) {
        self._settings.endedCb();
      }
    });

    this.sound.addEventListener('pause', () => {
      // The HTML5 audio player only has a pause(), no stop().
      // To differentiate between the two, a flag is set in case the user
      // explicitly stopped (not paused) the audio.
      if (self._pauseIsStop === true) {
        self._pauseIsStop = false;
        if (self._settings.pauseCb) {
          self._settings.pauseCb();
        }
      } else if (self._settings.stoppedCb) {
        self._settings.stoppedCb();
      }
      if (self._settings.playbackStoppedCb) {
        self._settings.playbackStoppedCb();
      }
    });

    this.sound.addEventListener('progress', () => {
      if (self._settings.progressCb) {
        self._settings.progressCb();
      }
    });

    this.sound.addEventListener('error', e => {
      switch (e.target.error.code) {
        case e.target.error.MEDIA_ERR_ABORTED:
          console.error('You aborted the playback.');
          break;
        case e.target.error.MEDIA_ERR_NETWORK:
          console.error(
          'A network error caused the audio download to fail.');
          break;
        case e.target.error.MEDIA_ERR_DECODE:
          console.error(
          'The audio playback was aborted due to a corruption ' +
          'problem or because the media used features your ' +
          'browser did not support.');
          break;
        case e.target.error.MEDIA_ERR_SRC_NOT_SUPPORTED:
          console.error(
          'The audio could not be loaded, either because the ' +
          'server or network failed or because the format is ' +
          'not supported.');
          break;
        default:
          console.error('An unknown error occurred.');
          break;
      }
      if (self._settings.errorCb) {
        self._settings.errorCb();
      }
    });
  }


  /**
   * Preload audio from an URL.
   *
   * @param {string} url - The URL that contains the audio.
   * @param {boolean} [preload=true] - Try preloading metadata and possible some audio (default).
   * Set to false to not download anything until playing.
   * @param {Function} [loadedCb] - The callback that is invoked when the duration of the audio file
   * is first known.
   */
  load(url, preload, loadedCb) {
    preload = preload === undefined ? true : preload;

    // Automatically begin buffering the file, even if autoplay is off.
    this.sound.autobuffer = Boolean(preload);

    // Preloading options:
    // none - Do not preload any media.
    // Wait for a play event before downloading anything.
    // metadata - Preload just the metadata. Grab the start and the end of
    // the file via range-request and determine the duration.
    // auto - Preload the whole file. Grab the start and the end of the
    // file to determine duration, then seek back to the start
    // again for the preload proper.
    this.sound.preload = preload ? 'auto' : 'none';

    const self = this;
    if (loadedCb) {
      this.sound.addEventListener('durationchange', () => {
        console.log('Duration change for ' + url + ' to : ' +
          self.sound.duration);
        loadedCb(self.sound);
      });
    }

    this.sound.src = url;
  }


  /**
   * Start or continue playback of audio.
   *
   * @param {number} [position] - When position is given, start playing from this position (seconds).
   */
  play(position) {
    if (position !== undefined) {
      if (this.sound.readyState < this.sound.HAVE_METADATA) {
        // In case the audio wasn't already preloaded, do it now.
        this.sound.preload = 'auto';
        console.warn('Playing from a given position is not possible. ' +
          'Audio was not yet loaded. Try again.');
      } else {
        console.debug('Scrub position to: ' + position);
        this.sound.currentTime = position;
      }
    }
    console.debug('Start playing from position: ' + this.sound.currentTime);
    this.sound.play();
  }

  /**
   * Unload previously loaded audio.
   */
  reset() {
    this._initPlayer();
  }

  /**
   * Stop playback of audio.
   */
  stop() {
    // The HTML5 audio player only has a pause(), no stop().
    // To differentiate between the two, set a flag.
    this.sound.pause();
    this.sound.currentTime = 0;
  }

  /**
   * Pause playback of audio.
   */
  pause() {
    this._pauseIsStop = true;
    this.sound.pause();
  }

  /**
   * Start preloading audio.
   */
  preload() {
    // In case the audio wasn't already preloaded, do it now.
    if (this.sound.preload !== 'auto') {
      console.info('Start preloading audio.');
      this.sound.preload = 'auto';
    }
  }


  /**
   * Start playing audio at the given offset.
   *
   * @param {number} percentage - Start at this percentage (0..100) of the audio stream.
   */
  scrub(percentage) {
    // In case the audio wasn't already preloaded, do it now.
    if (this.sound.readyState < this.sound.HAVE_METADATA) {
      this.preload();
      console.warn('Scrubbing not possible. Audio was not yet loaded. ' +
        'Try again.');
      return;
    }

    const newTime = this.sound.duration / 100 * percentage;
    console.log('Moving audio position to: ' + percentage + '%: ' +
      newTime + 's of total playing time: ' + this.sound.duration);
    this.sound.currentTime = newTime;
  }

  /**
   * Returns the percentage of which the buffer is filled.
   *
   * @returns {number} Percentage of buffer fill.
   */
  getBufferFill() {
    if (this.sound.buffered === undefined ||
      this.sound.buffered.length === 0) {
      // Nothing buffered yet.
      return 0;
    }

    // The fact that there's not one buffer segment is ignored here.
    // Truely representing the buffered state requires multiple
    // loading bars.
    // Usually, when user didn't seek yet, there are two segments:
    // Got segment from: 0 to: 187.63999938964844
    // Got segment from: 222.44700622558594 to: 228.1140899658203
    // The latter is gained when the HTML5 audio component tries to find
    // the total audio duration.
    // More info:
    // http://html5doctor.com/html5-audio-the-state-of-play/#time-ranges
    let probableEnd = 0;
    for (let i = 0; i < this.sound.buffered.length; i++) {
      const start = this.sound.buffered.start(i);
      const end = this.sound.buffered.end(i);
      // console.log('Got segment from: ' + start + ' to: ' + end);
      // Often, the segment that starts from 0 keeps growing and
      // indicates -most likely- the biggest buffer.
      if (start === 0) {
        probableEnd = end;
      }
    }

    // Round up,so the buffer won't get stuck on 99% when
    // duration and buffer are equal, except for some far decimal.
    const loaded = Math.round(probableEnd * 100 / this.sound.duration);
    console.log('Buffer filled to ' + loaded + '%');
    return loaded;
  }

  /**
   * Returns the current playing time as offset in seconds from the start.
   *
   * @returns {number} Time in seconds as offset from the start.
   */
  getCurrentTime() {
    return this.sound.currentTime;
  }

  /**
   * Returns the total duration in seconds.
   *
   * @returns {number} Time in seconds of fragment duration. 0 if no audio is loaded.
   */
  getDuration() {
    let duration = this.sound.duration;
    // When no audio is loaded, the duration may be NaN
    if (!duration) {
      duration = 0;
    }
    return duration;
  }

  /**
   * Returns state of the player.
   *
   * @returns {boolean} True when player is currently playing. False when paused or stopped.
   */
  isPlaying() {
    return !this.sound.paused;
  }

  /**
   * Returns ready state of the player.
   *
   * @returns {boolean} True when player is ready to start loading data or play. False when no audio is loaded
   * or preparing.
   */
  canPlay() {
    // Either the player is in a valid readyState (preloaded), or
    // the player has a source attached and doesn't show any loading error (non-preloaded).
    return this.sound.readyState >= this.sound.HAVE_METADATA ||
      this.sound.src && !this.sound.error;
  }
}

/**
 * WebAudioRecorder.
 */
export default class WebAudioRecorder {
  /**
   * Use 'low level' processing tooling to record audio and get a Wave
   * (audio/wav) encoded recording.
   *
   * Currently supported in all modern HTML5/WebAudio browsers.
   *
   * @param {GainNode} source - The source to record.
   * @param {Function} [streamingCallback] - The callback to deliver audio chunks to.
   * @param {WavePacker} packer - Packer to use.
   */
  constructor(source, streamingCallback, packer) {
    this.recording = false;

    const context = source.context;
    // For the best quality, use the samplerate in which audio is recorded.
    this.recordedSampleRate = context.sampleRate;
    // var sampleRate = recordedSampleRate;
    // 48000hz -> 24000hz recording, 44100hz -> 22050hz recording.
    // Sheffield determined the minimum to be 16000hz, so /4 is too low.
    this.sampleRate = this.recordedSampleRate / 2;
    // Streaming doesn't yet downsample: #1302.
    this.sampleRate = streamingCallback ? this.recordedSampleRate :
                        this.sampleRate;

    // Always record audio in mono.
    this.channels = 1;
    console.log('Recording at: ' +
                  this.getAudioSpecs().audioParameters.sampleRate);

    this.packer = packer;
    this.packer.init(this.recordedSampleRate, this.sampleRate, this.channels);

    // From the spec: This value controls how frequently the audioprocess
    // event is dispatched and how many sample-frames need to be processed
    // each call. Lower values for buffer size will result in a lower
    // (better) latency. Higher values will be necessary to avoid audio
    // breakup and glitches.
    // Legal values are (256, 512, 1024, 2048, 4096, 8192, 16384).
    const bufferSize = 8192;
    const recorder = context.createScriptProcessor(bufferSize, 2, 2);
    // Keep a reference to the scriptProcessor.
    // This is a workaround for a bug in Chrome that would otherwise lead to
    // the recorder being garbage collected before it even recorded anything.
    // https://bugs.webkit.org/show_bug.cgi?id=112521
    this._recorder = recorder;

    const self = this;
    recorder.onaudioprocess = function(e) {
      if (!self.recording) {
        return;
      }
      const left = e.inputBuffer.getChannelData(0);
      const right = e.inputBuffer.getChannelData(1);
      // These returned channel buffers are pointers to the current samples
      // coming in. Make a snapshot (clone). The webworkers can't serialize
      // the pointers. Well, Chrome and FF could, but Edge can't.
      const leftClone = new Float32Array(left);
      const rightClone = new Float32Array(right);
      self.packer.record(leftClone, rightClone);
      if (streamingCallback) {
        self.packer.recordStreaming(leftClone, rightClone, streamingCallback);
      }
    };

    source.connect(recorder);
    // If the script node is not connected to an output the "onaudioprocess"
    // event is not triggered in chrome.
    recorder.connect(context.destination);
  }

  /**
   * Get the recorded audio specifications.
   *
   * @returns {Object} Containing metadata on the audio format.
   */
  getAudioSpecs() {
    return {
      audioFormat: 'audio/wave',
      audioParameters: {
        channels: this.channels,
        sampleWidth: 16,
        frameRate: this.sampleRate,
        sampleRate: this.sampleRate
      }
    };
  }

  /**
   * Start recording audio.
   *
   */
  record() {
    this.packer.clear();
    this.recording = true;
  }

  /**
   * Request encoded audio to be returned through callback.
   *
   * @param {Function} callback - The callback to use when returning the audio as a blob in Wave format.
   */
  getEncodedAudio(callback) {
    this.packer.exportWAV(callback);
  }

  /**
   * Is audio recording in progress.
   *
   * @returns {boolean} True when recording. False otherwise.
   */
  isRecording() {
    return this.recording;
  }

  /**
   * Stop recording audio.
   */
  stop() {
    this.recording = false;
  }
}

/**
 * @title ITSLanguage Javascript
 * @overview This is part of the ITSLanguage Javascript SDK to perform helper functions.
 * @copyright (c) 2014 ITSLanguage
